frappe.ui.form.on('Delivery Note', {
    refresh(frm) {
        if (frm.doc.docstatus === 1) {
            frm.add_custom_button('Get Pass', async () => {
                // Prepare the new Get Pass document
                let get_pass_doc = {
                    doctype: 'Get Pass',
                    data: frm.doc.name,
                    date: frappe.datetime.now_date(),
                    delivery_note: frm.doc.name,
                    //sales_person: frm.doc.sales_person || frm.doc.owner,
                    items: []
                };

                // Add items from Delivery Note
                frm.doc.items.forEach(row => {
                    get_pass_doc.items.push({
                        item_code: row.item_code,
                        item_name: row.item_name,
                        qty: row.qty,
                        rate: row.rate,
                        uom: row.uom,
                        amount: row.amount
                    });
                });

                // Insert the Get Pass doc
                frappe.call({
                    method: 'frappe.client.insert',
                    args: {
                        doc: get_pass_doc
                    },
                    callback: function (r) {
                        if (!r.exc) {
                            frappe.msgprint(__('Get Pass created'));
                            frappe.set_route('Form', 'Get Pass', r.message.name);
                        }
                    }
                });
            }, 'Create');
        }
    }
});

frappe.ui.form.on('Stock Entry', {
    validate: async function(frm) {
        if (frm.doc.stock_entry_type !== "Contingency") return;

        const limited_warehouses = ["Mahlet - GTW", "Kindinew - GTW", "Nati - GTW"];
        const max_qty = 5;
        let errors = [];

        for (const item of frm.doc.items) {
            const target_wh = item.t_warehouse;
            if (!target_wh || !limited_warehouses.includes(target_wh)) continue;

            const item_code = item.item_code;

            let current_qty = 0;

            // Using frappe.call to fetch Bin value more reliably
            await frappe.call({
                method: "frappe.client.get_value",
                args: {
                    doctype: "Bin",
                    filters: {
                        item_code: item_code,
                        warehouse: target_wh
                    },
                    fieldname: "actual_qty"
                },
                callback: function(r) {
                    if (r.message) {
                        current_qty = r.message.actual_qty;
                    }
                }
            });

            const predicted_qty = current_qty + item.transfer_qty;

            if (predicted_qty > max_qty) {
                errors.push(
                    `âŒ ${item_code} in ${target_wh} will exceed allowed limit of ${max_qty}. ` +
                    `Current: ${current_qty}, Transfer: ${item.transfer_qty}, After: ${predicted_qty}`
                );
            }
        }

        if (errors.length > 0) {
            frappe.throw("ðŸš« Stock Transfer Blocked:\n\n" + errors.join("\n\n"));
        }
    }
});

frappe.ui.form.on('Delivery Note', {
    refresh(frm) {
        if (frm.doc.name) {
            frappe.require('https://cdn.jsdelivr.net/npm/jsbarcode@3.11.5/dist/JsBarcode.all.min.js', function() {
                const wrapper = frm.fields_dict.custom_bar_code.$wrapper;
                wrapper.html('<svg id="barcode-svg"></svg>');


                JsBarcode("#barcode-svg", frm.doc.name, {
                    format: "CODE128",
                    width: 2,
                    height: 50,
                    displayValue: false  // ðŸ‘ˆ hides the text under barcode
                });
            });
        }
    }
});

