frappe.ui.form.on('Salary Structure Assignment', {
    salary_structure: async function(frm) {
        if (frm.doc.salary_structure) {
            let salary_structure = await frappe.db.get_doc('Salary Structure', frm.doc.salary_structure);

            let base_earning = (salary_structure.earnings || []).find(earning => earning.salary_component === 'Basic');

            if (base_earning) {
                frm.set_value('base', base_earning.amount);
            } else {
                frm.set_value('base', 0);
                frappe.msgprint('Basic salary component not found in the selected Salary Structure.');
            }
        }
    }
});

frappe.ui.form.on('Additional Salary', {
    employee: async function(frm) {
        if (frm.doc.employee) {
            // Fetch Salary Structure Assignment for selected employee
            let assignments = await frappe.db.get_list('Salary Structure Assignment', {
                filters: {
                    employee: frm.doc.employee
                },
                fields: ['salary_structure'],
                limit: 1
            });

            console.log({ assignments });

            if (assignments.length === 0) {
                frm.set_value('base', 0);
                frappe.msgprint('No Salary Structure Assignment found for this employee.');
                return;
            }

            let salary_structure_name = assignments[0].salary_structure;
            console.log({ salary_structure_name });

            // Fetch the Salary Structure document
            let salary_structure = await frappe.db.get_doc('Salary Structure', salary_structure_name);
            console.log({ salary_structure });

            // Find Basic salary component
            let base_earning = (salary_structure.earnings || []).find(
                earning => earning.salary_component === 'Basic'
            );
            console.log({ base_earning });

            if (base_earning) {
                frm.set_value('custom_salary', base_earning.amount);
            } else {
                frm.set_value('custom_salary', 0);
                frappe.msgprint('Basic salary component not found in the Salary Structure.');
            }
        }
    }
});

