frappe.ui.form.on('Sales Invoice', {
    validate: function(frm) {
        if (frm.doc.is_pos === 1) {
            if (!frm.doc.custom_attach_payment_documents_here) {
                frappe.throw(__('Please attach payment documents before submitting the invoice.'));
            }
        }
    }
});

frappe.ui.form.on('Sales Order', {
    onload: function(frm) {
        // Check if Sales Team table is empty
        if (!frm.doc.sales_team || frm.doc.sales_team.length === 0) {

            // Get the current logged-in user
            let currentUser = frappe.session.user;

            // Find if this user has a Sales Person record
            frappe.call({
                method: 'frappe.client.get_value',
                args: {
                    doctype: 'Sales Person',
                    filters: { custom_user_id: currentUser },
                    fieldname: 'name'
                },
                callback: function(r) {
                    if (r.message && r.message.name) {
                        // Add new row to Sales Team
                        let row = frm.add_child('sales_team');
                        row.sales_person = r.message.name;
                        row.allocated_percentage = 100;  // Optional: set allocation
                        refresh_field('sales_team');
                    }
                }
            });
        }
    }
});

