frappe.ui.form.on('Sales Invoice', {
    after_save: async function(frm) {
        if (!frm.doc.customer) return;

        // Fetch customer fields
        await frappe.call({
            method: "frappe.client.get",
            args: {
                doctype: "Customer",
                name: frm.doc.customer
            },
            callback: function(response) {
                const customer = response.message;
                const credit_customer = customer.custom_is_credit_customer;
                console.log({credit_customer})
                const created_on = frappe.datetime.str_to_obj(customer.creation);
                const cut_off_date = frappe.datetime.str_to_obj("2025-07-21");

                const total_qty = frm.doc.total_qty || 0;
                console.log({total_qty})
                const is_fs = (frm.doc.custom_reference_no);
                console.log({is_fs})

                // If credit customer, incentive = 0
                if (credit_customer === 1) {
                    frm.set_value('custom_incentive_value', 0);
                    return;
                }

                const is_new_customer = created_on > cut_off_date;
                console.log({is_new_customer})

                let rate = 0;

                if (is_new_customer) {
                    rate = is_fs ? 10 : 15;
                } else {
                    rate = is_fs ? 3 : 5;
                }

                frm.set_value('custom_incentive_value', total_qty * rate);
            }
        });
    }
});
