      color: white;
      text-align: center;
      padding: 60px 0;
      position: relative;
      overflow: hidden;
    }

    .intro::before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background: url('https://images.unsplash.com/photo-1556740738-b6a63e27c4df?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80') center/cover;
      opacity: 0.1;
    }

