* { margin: 0; padding: 0; box-sizing: border-box; }
body {
  font-family: 'Segoe UI', Tahoma, sans-serif;
  background-color: #eef7fc;
  color: #333;
  line-height: 1.6;
}
a { color: #0277bd; text-decoration: none; }
.container { max-width: 1150px; margin: auto; padding: 2rem; }

/* Header */
.top-header {
  background: linear-gradient(to right, #0277bd, #80deea);
  color: white;
  padding: 3rem 0; text-align: center;
}
.top-header h1 { font-size: 2.7rem; margin-bottom: 0.5rem; }
.top-header p { font-size: 1.1rem; }

/* Intro */
.intro {
  background-color: #fff;
  padding: 2rem 0; border-bottom: 1px solid #ddd;
}
.intro h2 { color: #0277bd; margin-bottom: 1rem; }

/* Date Input */
.date-input-container {
  margin: 1rem 0;
  display: flex;
  align-items: center;
}
.date-input-container label { margin-right: 0.5rem; font-weight: 600; }
.date-input-container input {
  padding: 0.4rem; border: 1px solid #ccc;
  border-radius: 4px;
}

/* Table */
.responsive-table { overflow-x: auto; }
.quotation-table {
  width: 100%; border-collapse: collapse;
  background-color: #fff;
  box-shadow: 0 0 10px rgba(0,0,0,0.05);
  border-radius: 6px; overflow: hidden;
}
.quotation-table th, .quotation-table td {
  padding: 0.2rem 0.5rem; border-bottom: 1px solid #e0e0e0;
  font-size: 0.95rem; vertical-align: middle;
}
.quotation-table th {
  background-color: #e1f5fe;
  font-weight: 600;
}
.product-image {
  width: 100px; height: 100px; object-fit: contain;
  border: 1px solid #ddd; border-radius: 4px;
}
.no-image {
  width: 50px; height: 50px;
  background: #f0f0f0; color: #999;
  display: flex; align-items: center; justify-content: center;
  font-size: 0.7rem; border-radius: 4px;
}

/* Button */
.order-button-container {
  margin: 1.5rem 0; text-align: right;
}
.order-button {
  background-color: #0288d1;
  color: white;
  padding: 0.8rem 1.5rem;
  font-size: 1rem; font-weight: 600;
  border: none; border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.2s ease;
}
.order-button:hover {
  background-color: #01579b;
}

/* Message */
#order-message {
  margin-top: 1rem;
  font-size: 1rem;
}




.form-grid {
  display: flex;
  gap: 1.5rem;
  margin: 1.5rem 0;
  flex-wrap: wrap;
}

.form-group {
  display: flex;
  flex-direction: column;
  flex: 1;
  min-width: 250px;
}

.form-group label {
  font-weight: 600;
  margin-bottom: 0.4rem;
}

.form-group input {
  padding: 0.5rem;
  border: 1px solid #ccc;
  border-radius: 4px;
}



.auto-slider {
  display: flex;
  overflow: hidden;
  position: relative;
}
.product-card {
  flex: 0 0 calc(33.33% - 1rem);
  text-align: center;
  transition: transform 0.3s;
}
.product-card img {
  height: 260px;
  object-fit: contain;
  width: 100%;
  border-radius: 8px;
}
.product-card h4 {
  font-size: 1rem;
  /*color: #333;*/
}

/* Social Media Section */
.social-section {
  background: #2277bd;
  color: white;
  padding: 2rem 0;
  text-align: center;
}
.social-icons {
  display: flex;
  justify-content: center;
  gap: 2rem;
  margin-top: 1rem;
}
.social-icons a {
  color: white;
  font-size: 1.1rem;
  text-decoration: none;
}
.social-icons a i {
  margin-right: 0.5rem;
}

/* Total Calculation */
.totals {
  text-align: right;
  margin-top: 1rem;
  font-size: 1.1rem;
}

/* Rest of your previous CSS styles remain the same */



/* Navigation Bar */
.navbar {
  background-color: #1e3a8a;
  padding: 1rem 0;
  position: sticky;
  top: 0;
  z-index: 1000;
}
.navbar-brand h1 {
  color: white;
  font-size: 1.5rem;
  margin: 0;
}
.nav-links {
  list-style: none;
  display: flex;
  gap: 2rem;
  margin: 0;
}
.nav-links a {
  color: white;
  text-decoration: none;
  font-size: 1rem;
  transition: color 0.3s ease;
}
.nav-links a:hover {
  color: #3b82f6;
}
.nav-toggle {
  display: none;
  color: white;
  font-size: 1.5rem;
  cursor: pointer;
}
@media (max-width: 768px) {
  .nav-links {
    display: none;
    flex-direction: column;
    position: absolute;
    top: 60px;
    left: 0;
    width: 100%;
    background-color: #1e3a8a;
    padding: 1rem;
  }
  .nav-links.active {
    display: flex;
  }
  .nav-toggle {
    display: block;
  }
}

/* Hero Section */
.hero {
  text-align: center;
  padding: 100px 0;
  background: linear-gradient(135deg, #1e3a8a, #3b82f6);
  color: white;
}
.hero h1 {
  font-size: 3rem;
  margin-bottom: 1rem;
}
.hero p {
  font-size: 1.5rem;
  max-width: 800px;
  margin: 0 auto 2rem;
}
.cta-button {
  display: inline-block;
  padding: 12px 24px;
  background-color: #3b82f6;
  color: white;
  text-decoration: none;
  border-radius: 5px;
  font-size: 1.1rem;
  transition: background-color 0.3s ease;
}
.cta-button:hover {
  background-color: #1e3a8a;
}

/* Different Section */
.different-section {
  padding: 60px 0;
  background-color: #f9fafb;
}
.different-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
  margin-top: 20px;
}
.different-item {
  text-align: center;
  padding: 20px;
  background-color: white;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
}
.different-item i {
  font-size: 2rem;
  color: #1e3a8a;
  margin-bottom: 10px;
}
.different-item h3 {
  font-size: 1.2rem;
  color: #1e3a8a;
  margin-bottom: 10px;
}
.different-item p {
  font-size: 0.9rem;
  color: #666;
}

