frappe.ready(function () {
  let items = [];
  let slideIndex = 0;
  const tbody = document.getElementById("item-rows");
  const slider = document.getElementById("slider-track");
  const btn = document.getElementById("make-order-btn");
  const msg = document.getElementById("order-message");

  const subtotalEl = document.getElementById("subtotal");
  const vatEl = document.getElementById("vat");
  const grandTotalEl = document.getElementById("grand-total");

  // Modal creation function
  function createModal(link) {
    const modal = document.createElement("div");
    modal.className = "modal";
    modal.style.position = "fixed";
    modal.style.top = "0";
    modal.style.left = "0";
    modal.style.width = "100%";
    modal.style.height = "100%";
    modal.style.backgroundColor = "rgba(0, 0, 0, 0.5)";
    modal.style.display = "flex";
    modal.style.justifyContent = "center";
    modal.style.alignItems = "center";
    modal.style.zIndex = "1000";

    const modalContent = document.createElement("div");
    modalContent.style.backgroundColor = "#fff";
    modalContent.style.borderRadius = "10px";
    modalContent.style.width = "80%";
    modalContent.style.maxWidth = "800px";
    modalContent.style.height = "80%";
    modalContent.style.position = "relative";
    modalContent.style.overflow = "hidden";
    modalContent.style.boxShadow = "0 4px 8px rgba(0, 0, 0, 0.2)";

    const iframe = document.createElement("iframe");
    iframe.src = link;
    iframe.style.width = "100%";
    iframe.style.height = "100%";
    iframe.style.border = "none";

    const closeButton = document.createElement("button");
    closeButton.textContent = "Close";
    closeButton.style.position = "absolute";
    closeButton.style.top = "10px";
    closeButton.style.right = "10px";
    closeButton.style.padding = "8px 16px";
    closeButton.style.backgroundColor = "#1e3a8a";
    closeButton.style.color = "white";
    closeButton.style.border = "none";
    closeButton.style.borderRadius = "5px";
    closeButton.style.cursor = "pointer";
    closeButton.style.transition = "background-color 0.3s ease";
    closeButton.onmouseover = () => (closeButton.style.backgroundColor = "#3b82f6");
    closeButton.onmouseout = () => (closeButton.style.backgroundColor = "#1e3a8a");
    closeButton.onclick = () => document.body.removeChild(modal);

    modalContent.appendChild(iframe);
    modalContent.appendChild(closeButton);
    modal.appendChild(modalContent);
    document.body.appendChild(modal);
  }

  fetch("/api/method/erpnext.api.get_items_and_prices")
    .then(res => res.json())
    .then(res => {
      items = res.message || [];
      tbody.innerHTML = "";

      if (!items.length) {
        tbody.innerHTML = "<tr><td colspan='7'>No items found.</td></tr>";
        return;
      }

      // Create product table
      items.forEach((item, idx) => {
        const row = document.createElement("tr");
        row.innerHTML = `
          <td><input type="checkbox" data-idx="${idx}" class="item-checkbox"></td>
          <td>${idx + 1}</td>
          <td>${item.image ? `<img src="${item.image}" class="product-image">` : "No image"}</td>
          <td>${item.item_code}</td>
          <td>${item.item_name}</td>
          <td>${item.price.toLocaleString()}</td>
          <td><input type="number" class="qty-input" data-idx="${idx}" value="12" min="12" disabled style="width: 60px;" /></td>
        `;
        tbody.appendChild(row);
      });

      // Create slider cards (duplicate for continuous sliding)
      const sliderItems = items.filter((_, idx) => idx < 12);
      const duplicatedItems = [...sliderItems, ...sliderItems]; // Duplicate for seamless loop
      duplicatedItems.forEach((item, idx) => {
        const card = document.createElement("div");
        card.className = "slider-card";
        card.style.flex = "0 0 33.33%"; // Show 3 cards at a time
        card.style.minWidth = "0"; // Prevent overflow
        card.innerHTML = `
          <img src="${item.image}" style="width: 100%; height: 300px; object-fit: cover;" />
          <div class="slider-card-content">
            <h3 style="font-size: 1rem; align-text: center;">${item.item_name}</h3>
          </div>
        `;

        // Add click event for specific item codes to show modal
        const redirectLinks = {
          "0001": "https://www.applac.com/applac/159.html",
          "0002": "https://www.applac.com/applac_2_premium_follow_on_formula_800g/161.html",
          "0003": "https://www.applac.com/applac_3_premium_growing_up_formula_800g/162.html",
          "0004": "https://www.applac.com/applac_premature_infant_formula_300g/",
          "0005": "https://www.applac.com/applac_lactose_free_infant_formula_300g/"
        };

        if (redirectLinks[item.item_code]) {
          card.style.cursor = "pointer";
          card.addEventListener("click", () => {
            createModal(redirectLinks[item.item_code]);
          });
        }

        slider.appendChild(card);
      });

      // Continuous slider logic
      function slide() {
        slideIndex++;
        if (slideIndex >= sliderItems.length) {
          slideIndex = 0; // Reset to start for continuous loop
          slider.scrollTo({ left: 0, behavior: 'instant' }); // Instant reset to avoid visual jump
          setTimeout(() => {
            slider.scrollTo({
              left: slideIndex * slider.children[0].offsetWidth,
              behavior: 'smooth'
            });
          }, 50); // Small delay to ensure smooth transition
        } else {
          slider.scrollTo({
            left: slideIndex * slider.children[0].offsetWidth,
            behavior: 'smooth'
          });
        }
      }

      setInterval(slide, 1700); // Slide every 3 seconds
    });

  tbody.addEventListener("change", (e) => {
    if (e.target.classList.contains("item-checkbox")) {
      const idx = e.target.dataset.idx;
      const qty = tbody.querySelector(`.qty-input[data-idx="${idx}"]`);
      qty.disabled = !e.target.checked;
      calculateTotals();
    }
    if (e.target.classList.contains("qty-input")) {
      calculateTotals();
    }
  });

  function calculateTotals() {
    let subtotal = 0;

    document.querySelectorAll(".item-checkbox:checked").forEach(cb => {
      const idx = cb.dataset.idx;
      const item = items[idx];
      const qty = parseInt(document.querySelector(`.qty-input[data-idx="${idx}"]`).value);
      subtotal += qty * item.price;
    });

    const vat = subtotal * 0.15;
    const total = subtotal + vat;

    subtotalEl.textContent = subtotal.toLocaleString();
    vatEl.textContent = vat.toLocaleString();
    grandTotalEl.textContent = total.toLocaleString();
  }

  btn.addEventListener("click", () => {
    const date = document.getElementById("delivery-date").value;
    const name = document.getElementById("customer-name").value.trim();
    const phone = document.getElementById("phone-number").value.trim();
    const company = document.getElementById("company").value.trim();
    const checkedBoxes = tbody.querySelectorAll(".item-checkbox:checked");

    msg.textContent = "";

    if (!checkedBoxes.length) return msg.textContent = "üõë Please select at least one item.";
    if (!name) return msg.textContent = "üõë Please enter your full name.";
    if (!company) return msg.textContent = "üõë Please enter your company name.";
    if (!phone) return msg.textContent = "üõë Please enter your phone number.";
    if (!date) return msg.textContent = "üõë Please select a delivery date.";

    const items_data = Array.from(checkedBoxes).map(cb => {
      const idx = cb.dataset.idx;
      const qty = parseInt(document.querySelector(`.qty-input[data-idx="${idx}"]`).value);
      return { item_code: items[idx].item_code, qty };
    });

    fetch("/api/method/erpnext.api.create_sales_order", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        full_name: name,
        phone: phone,
        delivery_date: date,
        company: company,
        items: items_data
      })
    })
      .then(res => res.json())
      .then(res => {
        if (res.message?.name || res.name) {
          msg.innerHTML = `‚úÖ Order <strong>${res.message?.name || res.name}</strong> placed! <a href="/app/sales-order/${res.message?.name || res.name}" target="_blank">View</a>`;
        } else {
          msg.textContent = "‚ùå Error placing order.";
        }
      });
  });
});

document.getElementById("send-gps-btn").addEventListener("click", function () {
  const statusEl = document.getElementById("gps-status");

  if (!navigator.geolocation) {
    statusEl.textContent = "‚ùå Geolocation is not supported by your browser.";
    return;
  }

  statusEl.textContent = "üìç Getting your location...";

  navigator.geolocation.getCurrentPosition(
    async (position) => {
      const lat = position.coords.latitude;
      const long = position.coords.longitude;

      const now = new Date();
      const timestamp = now.toISOString();
      const dateOnly = now.toISOString().split("T")[0]; // YYYY-MM-DD

      const payload = {
        employee: frappe.session.user,
        latitude: lat,
        longitude: long,
        timestamp: timestamp,
        date: dateOnly
      };

      try {
        const res = await fetch("/api/resource/GPS Location Log", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "X-Frappe-CSRF-Token": frappe.csrf_token
          },
          body: JSON.stringify({ data: payload })
        });

        if (res.ok) {
          statusEl.textContent = "‚úÖ Location sent successfully!";
        } else {
          const err = await res.json();
          console.error(err);
          statusEl.textContent = "‚ùå Failed to send location.";
        }
      } catch (err) {
        console.error(err);
        statusEl.textContent = "‚ùå Error sending location.";
      }
    },
    (err) => {
      statusEl.textContent = "‚ùå Location error: " + err.message;
    }
  );
});
